"use strict";
import React, { Component } from "react";
import Web3 from "web3";
import { SIMP_STORAGE_ADDRESS, SIMP_STORAGE_ABI } from "./config.js";

import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import "./App.css";
import QrReader from "react-qr-reader";
import { Col, Row, Card } from "react-bootstrap";
class App extends Component {
  state = {
    result: "No result",
  };

  myFunction =() =>{
  var popup = document.getElementById("myPopup");
  popup.classList.toggle("show");

  }

  handleScan = (data) => {
    if (data) {
      this.setState({
        result: data,
        msg: "Scan Successful",
        dis: false,
      });
    }
  };
  handleError = (err) => {
    console.error(err);
  };

  constructor() {
    super();
    this.state = {
      account: "No address associated",
      val: "",
      result: " ",
      dis: true,
      msg: "Hold still!",
      msg2: "",
      simpcontract: "",
      value: "",
      value2: "",
      hash: "",
      hash2: "",
      isverify: "",
      va: "",
    };
  }

  componentDidMount() {
    this.loadBlockchainData();
    this.loadBlockchainData();
  }

  async loadBlockchainData() {
    const web3 = new Web3("HTTP://127.0.0.1:7545");
    const simpstorage = new web3.eth.Contract(
      SIMP_STORAGE_ABI,
      SIMP_STORAGE_ADDRESS
    );
    this.setState({ simpcontract: simpstorage });

    var msg = "";
  }

  async sign(web3, text, addr) {
    await web3.eth.sign("0x" + this.toHex(text), addr).then((result) => {
      this.setState({
        signature: result.substr(2),
      });
    });
    this.state.r = this.state.signature.slice(0, 64);
    this.state.s = this.state.signature.slice(64, 128);
    this.state.v = this.state.signature.slice(128, 130);
    //this.state.v_dec=web3.utils.toDecimal(this.state.v)
    console.log(this.state.signature);
    console.log(this.state.r);
    console.log(this.state.s);
    console.log(this.state.v_dec);
  }

  async getHashQR(cnic, simpstorage) {
    const value = await simpstorage.methods
      .calculateHash(cnic)
      .call()
      .then((res) => {
        this.setState({ hash: res });
      });

    // this.setState({value: value})
    // console.log(this.state.value)
  }
  async getHash(cnic, simpstorage) {
    const value = await simpstorage.methods
      .calculateHash(cnic)
      .call()
      .then((res) => {
        this.setState({ hash2: res });
      });
  }
  parsePetitionList(str) {
    // return str
    var str2 = "";
    var x = str.split(",");
    for (var i = 0; i < x.length; i++) {
      str2 += "\npetitioner: ";
      str2 += x[i];

      str2 += "\nname: ";
      str2 += x[i + 1];

      str2 += ", time: ";
      str2 += x[i + 2];

      str2 += ", votes: ";
      str2 += x[i + 3];
      str2 += "\n";
      i += parseInt(x[i + 3]);
      if (parseInt(x[i + 3]) == 0) {
        i++;
      }
      i += 3;
      i++;
      str2 += "\n";
    }
    return str2;
  }

  toHex(str) {
    var hex = "";
    for (var i = 0; i < str.length; i++) {
      hex += "" + str.charCodeAt(i).toString(16);
    }
    return hex;
  }

  async getUserData(id, simpstorage) {
    const value = await simpstorage.methods
      .getVaccinationRecord(id.toString())
      .call({ from: this.state.account })
      .catch((error) => {
        alert("User Vaccination record doesn't exist/ Not Authorized");
      });
    console.log(value);

    if (value != null) {
      const val = this.parseUserData(value.toString());
      this.setState({ value: val.toString() });
    } else {
      this.setState({ value: "not found" });
    }
  }
  async getUserDataForOthers(id, simpstorage) {
    const value = await simpstorage.methods
      .getVaccinationHash(id.toString())
      .call()
      .catch((error) => {
        alert("User Vaccination record doesn't exist");
      });

    if (value != null) {
      this.setState({ value2: value });
      if (this.state.hash == this.state.value2) {
        this.setState({ isverify: "QR Verified with Blockchain" });
      } else if (this.state.hash2 == this.state.value2) {
        this.setState({ isverify: "Hash Verified with Blockchain" });
      } else {
        this.setState({ isverify: "Hash DOES NOT Verify with Blockchain" });
      }
    }
  }
  parseUserData(str) {
    // return str
    var str2 = "";
    var x = str.split(",");
    for (var i = 0; i < x.length; i++) {
      if (i == 0) {
        str2 += "\nCNIC/ID: ";
        str2 += x[i];
      } else if (i == 1) {
        str2 += "\nUser_address: ";
        str2 += x[i];
      } else if (i == 2) {
        str2 += "\nCountry: ";
        str2 += x[i];
      } else if (i == 3) {
        str2 += ", Name: ";
        str2 += x[i];
      } else if (i == 4) {
        str2 += ", Gender: ";
        // str2 += x[i]
        if (x[i] == "false") {
          str2 += "Male";
        } else if (x[i] == "true") {
          str2 += "Female";
        }
      } else if (i == 5) {
        str2 += "\nCreated by: ";
        str2 += x[i];
      } else if (i == 6) {
        str2 += "\nHash: ";
        str2 += x[i];
      } else {
        str2 += "\nVaccination: ";
        str2 += x[i];
        str2 += ",BatchID: ";
        str2 += x[i + 1];
        str2 += "\nAdded by: ";
        str2 += x[i + 2];
        str2 += ",AdministrationDate: ";
        str2 += x[i + 3];
        str2 += ", Location: ";
        str2 += x[i + 4];
        i += 4;
      }
    }

    return str2;
  }
  async getPetitionData(simpstorage) {
    const val = await simpstorage.methods.viewPetitions().call();
    const val2 = this.parsePetitionList(val.toString());
    this.setState({ petitions: val2 });
  }

  handleSubmit(e) {
    e.preventDefault();
    e.target.reset();
  }
  async getVAData(simpstorage) {
    const value = await simpstorage.methods.getVaccinationAuthorities().call();
    const val = this.parseList(value.toString());
    this.setState({ va: val });
  }
  async getmsg() {
    this.setState({ msg2: "Vote Casted" });
  }
  parseList(str) {
    var str2 = "";
    for (var i = 0; i < str.length; i++) {
      if (str[i] == ",") {
        str2 += "\n";
      } else {
        str2 += str[i];
      }
    }
    return str2;
  }
  render() {
    return (
      <div className="App-header">
        <div className="bg-card" style={{ borderRadius: "4rem" }}>
          {/*<div className="image">*/}
          <img
            className="image"
            src="https://www.brookings.edu/wp-content/uploads/2021/01/2021-01-25T194508Z_1_LYNXMPEH0O1D7_RTROPTP_4_HEALTH-CORONAVIRUS-FRANCE-VACCINATION.jpg"
            style={{  width: "30rem", height: "30rem" }}
          />


          {/*</div>*/}
          {/*<div className="tabs">*/}
          <Tabs className="tabs">
            <TabList style={{}}>
              <Tab className="tab active"><b>Register user</b></Tab>
              <Tab className="tab"><b>Register Vaccination</b></Tab>
              <Tab className="tab"><b>Get Record</b></Tab>
              <Tab className="tab"><b>Get Hash/ Verify</b></Tab>
              <Tab className="tab"><b>Vote</b></Tab>
              <Tab className="tab"><b>Add Petition</b></Tab>
            </TabList><br/><br/><br/>

            <TabPanel style={{}}>
              {/*  // function registerUser(string memory _id, string memory _name, bool _gender, string memory _nationality,*/}
              {/*//  address _user_address) public onlyVaccinationAuthority{*/}
              <div>
                <h1 className="green"> Welcome! </h1>
                <form
                  onSubmit={(event) => {
                    event.preventDefault();
                    const accNum = new FormData(event.target).get("accountNum");
                    this.state.account = accNum;
                    this.setState({ account: accNum });
                    this.handleSubmit(event);
                  }}
                >
                <h4 className="green">Using Account at Address:</h4>
                <input
                    id="acnum"
                    name="accountNum"
                    type="accnum"
                    defaultValue={this.props.username}
                    required
                    style={{background: "#ffffff" ,fontSize:"20px"}}
                  />
                  <input
                    type="submit"
                    hidden=""
                    value="Use Address"
                    style={{
                      width: "8rem",
                      background: "#308D46",
                      marginLeft: "1rem",
                      fontSize:"20px"
                    }}
                  />
                </form>
                <h4>USER REGISTERATION: Enter the following details:</h4>


                <form
                  onSubmit={(event) => {
                    event.preventDefault();

                    const cnic = new FormData(event.target).get("cnic");
                    const name = new FormData(event.target).get("name");
                    const countryName = new FormData(event.target).get(
                     "countryName"
                    );
                    const gender = new FormData(event.target).get("gender");
                    const addr = new FormData(event.target).get("addr");

                    this.state.simpcontract.methods
                      .registerUser(cnic, name, gender, countryName, addr)
                      .send({ from: this.state.account, gas: 900000 })
                      .catch((error) => {
                        this.setState({
                          err2: "User already exists/only Authority is allowed to register",
                        });
                      });
                    this.loadBlockchainData();
                    this.handleSubmit(event);
                  }}
                >
                  <input
                    id="cnic" 
                    className="wrapbox"
                    name="cnic"
                    type="text"
